
var Footprints = require('./footprints.js');
var SingleFootprint = require('./singlefootprint.js');

var footprintsResFactory = function (webreq) {
    if (webreq.search.id)
        return new SingleFootprint(webreq);
    else
        return new Footprints(webreq);
};

module.exports = footprintsResFactory;
