
var resxutil = require('resxutil');

exports.getFields = { _id: 1, actId: 1, createdOn: 1, userId: 1, statusId: 1 };
exports.convFootprintInfo = function (mem, acts) {
    var ret = { id: mem._id.toString(), createdOn: mem.createdOn, statusId: mem.statusId };
    var activity = acts.filter(function (a) { return a._id.equals(mem.actId); })[0];
    ret.activity = activity ? resxutil.activity.convRelatedActivityInfo(activity) : null;
    return ret;
};
