
var SiteIdles = require('./siteidles.js');
var SingleSiteIdle = require('./singlesiteidle.js');

var siteIdlesResFactory = function (webreq) {
    if (webreq.search.id)
        return new SingleSiteIdle(webreq);
    else
        return new SiteIdles(webreq);
};

module.exports = siteIdlesResFactory;
