
var SitePreviews = require('./sitepreview.js');
var SingleSitePreview = require('./singlesitepreview.js');

var sitepreviewResFactory = function (webreq) {
    if (webreq.search.siteId)
        return new SingleSitePreview(webreq);
    else
        return new SitePreviews(webreq);
};

module.exports = sitepreviewResFactory;
