
var VendorPreviews = require('./vendorpreview.js');
var SingleVendorPreview = require('./singlevendorpreview.js');

var vendorpreviewsResFactory = function (webreq) {
    if (webreq.search.vendorId)
        return new SingleVendorPreview(webreq);
    else
        return new VendorPreviews(webreq);
};

module.exports = vendorpreviewsResFactory;
