
var db = require('dbaccess');
var bluebird = require('bluebird');
var extension = require('extension');
var constants = require('const');
var imageUpload = require('imageUpload');
var errdef = require('errdef');
var resxutil = require('resxutil');
var config = require('config');
var Resource = require('resource');
var validate = require('validate');
var infra = require('infra');

/*
'id':,
'name':,
'logoUrl':
*/
var SingleVendorPreview = function (webreq) {
    var me = this;
    Resource.call(me, webreq);
    me.filter.vendorObjId = extension.mongo.tryConvObjId(webreq.search.vendorId);
};

SingleVendorPreview.prototype = Object.create(Resource.prototype);

SingleVendorPreview.prototype._accessibility = function () {
    var httpOptions = new resxutil.HttpOptions();
    return bluebird.resolve(httpOptions.setStatus('GET', this.filter.vendorObjId ? 200 : 400));
};

SingleVendorPreview.prototype._get = function () {
    var me = this;
    return me._accessibility()
    .then(function (httpOptions) {
        var status = httpOptions.getStatus('GET');
        if (status.isOK())
            return db.vendor.getOneVendorFieldsBy({ _id: me.filter.vendorObjId }, resxutil.vendorpreview.getFields);
        else
            throw new errdef.DeferChainKiller(status.toWebres());
    })
    .then(function (vd) {
        if (vd)
            return resxutil.vendorpreview.convVendorPreview(vd);
        else
            throw new errdef.DeferChainKiller(extension.http.webres404());
    })
    .then(function (data) {
        return extension.http.webres200(constants.mime.json, JSON.stringify(data));
    })
    .catch(errdef.DeferChainKiller, function (err) {
        return err.attach;
    });
};

module.exports = SingleVendorPreview;