
var db = require('dbaccess');
var bluebird = require('bluebird');
var extension = require('extension');
var constants = require('const');
var imageUpload = require('imageUpload');
var errdef = require('errdef');
var resxutil = require('resxutil');
var config = require('config');
var Resource = require('resource');
var validate = require('validate');
var _ = require('underscore');
var infra = require('infra');

var wxsecret = new infra.WxSecret(config.wx);

var wxResFactory = function (webreq) {
    return new WxJsApiConfig(webreq);
};

var WxJsApiConfig = function (webreq) {
    Resource.call(this, webreq);
    this.url = webreq._raw.headers.referer;
};

WxJsApiConfig.prototype = Object.create(Resource.prototype);

WxJsApiConfig.prototype._accessibility = function () {
    var httpOptions = new resxutil.HttpOptions();
    return bluebird.resolve(httpOptions.setStatus('GET', 200));
};

WxJsApiConfig.prototype._get = function () {
    var me = this;
    return me._accessibility()
    .then(function (httpOptions) {
        var status = httpOptions.getStatus('GET');
        if (status.isOK())
            return wxsecret.getJsApiConfig(me.url);
        else
            throw new errdef.DeferChainKiller(status.toWebres());
    })
    .then(function (obj) {
        return extension.http.webres200(constants.mime.json, JSON.stringify(obj));
    })
    .catch(errdef.DeferChainKiller, function (err) {
        return err.attach;
    });
};

module.exports = wxResFactory;
