
var bluebird = require('bluebird');
var config = require('config');
var mongoclient = require('./mongoclient.js');

var dbActivityProxy = {};

dbActivityProxy.getOneActFieldsBy = function (filter, fields) {
    return mongoclient.getOneDocFields(config.dbCols.activities, filter, fields);
};

dbActivityProxy.getActsFieldsBy = function (filter, fields) {
    return mongoclient.getDocsFields(config.dbCols.activities, filter, fields);
};

dbActivityProxy.getActsFieldsInPage = function (filter, fields, orderby, skip, take) {
    return mongoclient.getDocsFieldsInPage(config.dbCols.activities, filter, fields, orderby, skip, take);
};

dbActivityProxy.updateActs = function (filter, update) {
    return mongoclient.updateDocs(config.dbCols.activities, filter, update);
};

dbActivityProxy.insertOneAct = function (act) {
    return mongoclient.insertOneDoc(config.dbCols.activities, act);
};

dbActivityProxy.removeActs = function (filter) {
    return mongoclient.removeDocs(config.dbCols.activities, filter);
};

dbActivityProxy.countActs = function (filter) {
    return mongoclient.countDocs(config.dbCols.activities, filter);
};

dbActivityProxy.mapReduce = function (mapFn, reduceFn, query) {
    return mongoclient.mapReduce(config.dbCols.activities, mapFn, reduceFn, query);
};

module.exports = dbActivityProxy;
