
var bluebird = require('bluebird');
var config = require('config');
var mongoclient = require('./mongoclient.js');

var dbMemberProxy = {};

dbMemberProxy.getOneMemberFieldsBy = function (filter, fields) {
    return mongoclient.getOneDocFields(config.dbCols.members, filter, fields);
};

dbMemberProxy.getMembersFieldsBy = function (filter, fields) {
    return mongoclient.getDocsFields(config.dbCols.members, filter, fields);
};

dbMemberProxy.getMembersFieldsInPage = function (filter, fields, orderby, skip, take) {
    return mongoclient.getDocsFieldsInPage(config.dbCols.members, filter, fields, orderby, skip, take);
};

dbMemberProxy.updateMembers = function (filter, update) {
    return mongoclient.updateDocs(config.dbCols.members, filter, update);
};

dbMemberProxy.insertOneMember = function (member) {
    return mongoclient.insertOneDoc(config.dbCols.members, member);
};

dbMemberProxy.removeMembers = function (filter) {
    return mongoclient.removeDocs(config.dbCols.members, filter);
};

dbMemberProxy.countMembers = function (filter) {
    return mongoclient.countDocs(config.dbCols.members, filter);
};

module.exports = dbMemberProxy;
