
var bluebird = require('bluebird');
var config = require('config');
var mongoclient = require('./mongoclient.js');

var dbMsgProxy = {};

dbMsgProxy.getOneMessageFieldsBy = function (filter, fields) {
    return mongoclient.getOneDocFields(config.dbCols.messages, filter, fields);
};

dbMsgProxy.getMessagesFieldsBy = function (filter, fields) {
    return mongoclient.getDocsFields(config.dbCols.messages, filter, fields);
};

dbMsgProxy.getMessagesFieldsInPage = function (filter, fields, orderby, skip, take) {
    return mongoclient.getDocsFieldsInPage(config.dbCols.messages, filter, fields, orderby, skip, take);
};

dbMsgProxy.updateMessages = function (filter, update) {
    return mongoclient.updateDocs(config.dbCols.messages, filter, update);
};

dbMsgProxy.insertOneMessage = function (msg) {
    return mongoclient.insertOneDoc(config.dbCols.messages, msg);
};

dbMsgProxy.removeMessages = function (filter) {
    return mongoclient.removeDocs(config.dbCols.messages, filter);
};

dbMsgProxy.countMessages = function (filter) {
    return mongoclient.countDocs(config.dbCols.messages, filter);
};

module.exports = dbMsgProxy;
