
var bluebird = require('bluebird');
var config = require('config');
var mongoclient = require('./mongoclient.js');

var dbNoteProxy = {};

dbNoteProxy.getOneNoteFieldsBy = function (filter, fields) {
    return mongoclient.getOneDocFields(config.dbCols.notes, filter, fields);
};

dbNoteProxy.getNotesFieldsBy = function (filter, fields) {
    return mongoclient.getDocsFields(config.dbCols.notes, filter, fields);
};

dbNoteProxy.getNotesFieldsInPage = function (filter, fields, orderby, skip, take) {
    return mongoclient.getDocsFieldsInPage(config.dbCols.notes, filter, fields, orderby, skip, take);
};

dbNoteProxy.updateNotes = function (filter, update) {
    return mongoclient.updateDocs(config.dbCols.notes, filter, update);
};

dbNoteProxy.insertOneNote = function (note) {
    return mongoclient.insertOneDoc(config.dbCols.notes, note);
};

dbNoteProxy.insertNotes = function (notes) {
    return mongoclient.insertDocs(config.dbCols.notes, notes);
};

dbNoteProxy.removeNotes = function (filter) {
    return mongoclient.removeDocs(config.dbCols.notes, filter);
};

dbNoteProxy.countNotes = function (filter) {
    return mongoclient.countDocs(config.dbCols.notes, filter);
};

module.exports = dbNoteProxy;
