
var bluebird = require('bluebird');
var config = require('config');
var mongoclient = require('./mongoclient.js');

var dbPwdresetProxy = {};

dbPwdresetProxy.getOnePwdresetFieldsBy = function (filter, fields) {
    return mongoclient.getOneDocFields(config.dbCols.pwdreset, filter, fields);
};

dbPwdresetProxy.getPwdresetsFieldsBy = function (filter, fields) {
    return mongoclient.getDocsFields(config.dbCols.pwdreset, filter, fields);
};

dbPwdresetProxy.getPwdresetsFieldsInPage = function (filter, fields, orderby, skip, take) {
    return mongoclient.getDocsFieldsInPage(config.dbCols.pwdreset, filter, fields, orderby, skip, take);
};

dbPwdresetProxy.updatePwdresets = function (filter, update) {
    return mongoclient.updateDocs(config.dbCols.pwdreset, filter, update);
};

dbPwdresetProxy.insertOnePwdreset = function (pr) {
    return mongoclient.insertOneDoc(config.dbCols.pwdreset, pr);
};

dbPwdresetProxy.removePwdresets = function (filter) {
    return mongoclient.removeDocs(config.dbCols.pwdreset, filter);
};

dbPwdresetProxy.countPwdresets = function (filter) {
    return mongoclient.countDocs(config.dbCols.pwdreset, filter);
};

module.exports = dbPwdresetProxy;
