
var bluebird = require('bluebird');
var config = require('config');
var mongoclient = require('./mongoclient.js');

var dbSessionProxy = {};

dbSessionProxy.getOneSessionFieldsBy = function (filter, fields) {
    return mongoclient.getOneDocFields(config.dbCols.sessions, filter, fields);
};

dbSessionProxy.updateSessions = function (filter, update) {
    return mongoclient.updateDocs(config.dbCols.sessions, filter, update);
};

dbSessionProxy.insertOneSession = function (session) {
    return mongoclient.insertOneDoc(config.dbCols.sessions, session);
};

dbSessionProxy.removeSessions = function (filter) {
    return mongoclient.removeDocs(config.dbCols.sessions, filter);
};

dbSessionProxy.findAndModifySession = function (filter, update, fields, wannaNew) {
    return mongoclient.findAndModify(config.dbCols.sessions, filter, update, fields, wannaNew);
};

module.exports = dbSessionProxy;
