
var bluebird = require('bluebird');
var config = require('config');
var mongoclient = require('./mongoclient.js');

var dbSiteProxy = {};

dbSiteProxy.getOneSiteFieldsBy = function (filter, fields) {
    return mongoclient.getOneDocFields(config.dbCols.sites, filter, fields);
};

dbSiteProxy.getSitesFieldsBy = function (filter, fields) {
    return mongoclient.getDocsFields(config.dbCols.sites, filter, fields);
};

dbSiteProxy.getSitesFieldsInPage = function (filter, fields, orderby, skip, take) {
    return mongoclient.getDocsFieldsInPage(config.dbCols.sites, filter, fields, orderby, skip, take);
};

dbSiteProxy.updateSites = function (filter, update) {
    return mongoclient.updateDocs(config.dbCols.sites, filter, update);
};

dbSiteProxy.insertOneSite = function (site) {
    return mongoclient.insertOneDoc(config.dbCols.sites, site);
};

dbSiteProxy.removeSites = function (filter) {
    return mongoclient.removeDocs(config.dbCols.sites, filter);
};

dbSiteProxy.countSites = function (filter) {
    return mongoclient.countDocs(config.dbCols.sites, filter);
};

module.exports = dbSiteProxy;
