
var bluebird = require('bluebird');
var config = require('config');
var mongoclient = require('./mongoclient.js');

var dbSiteIdleProxy = {};

dbSiteIdleProxy.getOneSiteIdleFieldsBy = function (filter, fields) {
    return mongoclient.getOneDocFields(config.dbCols.siteidles, filter, fields);
};

dbSiteIdleProxy.getSiteIdlesFieldsBy = function (filter, fields) {
    return mongoclient.getDocsFields(config.dbCols.siteidles, filter, fields);
};

dbSiteIdleProxy.getSiteIdlesFieldsInPage = function (filter, fields, orderby, skip, take) {
    return mongoclient.getDocsFieldsInPage(config.dbCols.siteidles, filter, fields, orderby, skip, take);
};

dbSiteIdleProxy.updateSiteIdles = function (filter, update) {
    return mongoclient.updateDocs(config.dbCols.siteidles, filter, update);
};

dbSiteIdleProxy.insertOneSiteIdle = function (idle) {
    return mongoclient.insertOneDoc(config.dbCols.siteidles, idle);
};

dbSiteIdleProxy.removeSiteIdles = function (filter) {
    return mongoclient.removeDocs(config.dbCols.siteidles, filter);
};

dbSiteIdleProxy.countSiteIdles = function (filter) {
    return mongoclient.countDocs(config.dbCols.siteidles, filter);
};

module.exports = dbSiteIdleProxy;
