
var bluebird = require('bluebird');
var config = require('config');
var mongoclient = require('./mongoclient.js');

var dbSmsVCProxy = {};

dbSmsVCProxy.getOneSmsVCFieldsBy = function (filter, fields) {
    return mongoclient.getOneDocFields(config.dbCols.smsvc, filter, fields);
};

dbSmsVCProxy.getSmsVCsFieldsBy = function (filter, fields) {
    return mongoclient.getDocsFields(config.dbCols.smsvc, filter, fields);
};

dbSmsVCProxy.getSmsVCsFieldsInPage = function (filter, fields, orderby, skip, take) {
    return mongoclient.getDocsFieldsInPage(config.dbCols.smsvc, filter, fields, orderby, skip, take);
};

dbSmsVCProxy.updateSmsVCs = function (filter, update) {
    return mongoclient.updateDocs(config.dbCols.smsvc, filter, update);
};

dbSmsVCProxy.insertOneSmsVC = function (smsvc) {
    return mongoclient.insertOneDoc(config.dbCols.smsvc, smsvc);
};

dbSmsVCProxy.removeSmsVCs = function (filter) {
    return mongoclient.removeDocs(config.dbCols.smsvc, filter);
};

dbSmsVCProxy.countSmsVCs = function (filter) {
    return mongoclient.countDocs(config.dbCols.smsvc, filter);
};

module.exports = dbSmsVCProxy;
