
var bluebird = require('bluebird');
var config = require('config');
var mongoclient = require('./mongoclient.js');

var dbUserProxy = {};

dbUserProxy.getOneUserFieldsBy = function (filter, fields) {
    return mongoclient.getOneDocFields(config.dbCols.users, filter, fields);
};

dbUserProxy.getUsersFieldsBy = function (filter, fields) {
    return mongoclient.getDocsFields(config.dbCols.users, filter, fields);
};

dbUserProxy.getUsersFieldsInPage = function (filter, fields, orderby, skip, take) {
    return mongoclient.getDocsFieldsInPage(config.dbCols.users, filter, fields, orderby, skip, take);
};

dbUserProxy.updateUsers = function (filter, update) {
    return mongoclient.updateDocs(config.dbCols.users, filter, update);
};

dbUserProxy.insertOneUser = function (user) {
    return mongoclient.insertOneDoc(config.dbCols.users, user);
};

dbUserProxy.removeUsers = function (filter) {
    return mongoclient.removeDocs(config.dbCols.users, filter);
};

dbUserProxy.countUsers = function (filter) {
    return mongoclient.countDocs(config.dbCols.users, filter);
};

module.exports = dbUserProxy;
