
var bluebird = require('bluebird');
var config = require('config');
var mongoclient = require('./mongoclient.js');

var dbVendorProxy = {};

dbVendorProxy.getOneVendorFieldsBy = function (filter, fields) {
    return mongoclient.getOneDocFields(config.dbCols.vendors, filter, fields);
};

dbVendorProxy.getVendorsFieldsBy = function (filter, fields) {
    return mongoclient.getDocsFields(config.dbCols.vendors, filter, fields);
};

dbVendorProxy.getVendorsFieldsInPage = function (filter, fields, orderby, skip, take) {
    return mongoclient.getDocsFieldsInPage(config.dbCols.vendors, filter, fields, orderby, skip, take);
};

dbVendorProxy.updateVendors = function (filter, update) {
    return mongoclient.updateDocs(config.dbCols.vendors, filter, update);
};

dbVendorProxy.insertOneVendor = function (vendor) {
    return mongoclient.insertOneDoc(config.dbCols.vendors, vendor);
};

dbVendorProxy.removeVendors = function (filter) {
    return mongoclient.removeDocs(config.dbCols.vendors, filter);
};

dbVendorProxy.countVendors = function (filter) {
    return mongoclient.countDocs(config.dbCols.vendors, filter);
};

module.exports = dbVendorProxy;
