
//BadArgumentsError
var BadArgumentsError = function (msg) {
    this._stack = (new Error()).stack;
    this.name = 'BadArgumentsError';
    this.message = msg;
};
BadArgumentsError.prototype = Object.create(Error.prototype);
Object.defineProperty(BadArgumentsError.prototype, 'stack', {
    get: function () {
        return this._stack;
    }
});

//ImageUploadError
var ImageUploadError = function (inner) {
    this.name = 'ImageUploadError';
    this.inner = inner;
};
ImageUploadError.prototype = Object.create(Error.prototype);
Object.defineProperty(ImageUploadError.prototype, 'stack', {
    get: function () {
        return this.inner ? this.inner.stack : '';
    }
});

//DeferChainKiller
var DeferChainKiller = function (attach) {
    this.attach = attach;
};
DeferChainKiller.prototype = Object.create(Error.prototype);

