var crypto = require('crypto');

var sha1 = function (input) {
    var shasum = crypto.createHash('sha1');
    shasum.update(input);
    return shasum.digest('hex');
};

var md5 = function (input) {
    var md5 = crypto.createHash('md5');
    md5.update(input);
    return md5.digest('hex');
};

exports.sha1 = sha1;
exports.md5 = md5;