var convPageSize = function (input, max, min, defaultVal) {
    var nInput = Number(input);
    if (isNaN(nInput))
        return defaultVal;
    else {
        if (!isNaN(min) && nInput < min)
            return min;
        if (!isNaN(max) && nInput > max)
            return max;
        return nInput;
    }
};

var convPage = function (input) {
    var nInput = Number(input);
    return isNaN(nInput) || nInput < 0 ? 0 : nInput;
};

exports.convPageSize = convPageSize;
exports.convPage = convPage;