
var getRandomWithTimestamp = function () {
    return getTimestamp() + getRandom();
};

// 0 -- 10000000000
var getRandom = function () {
    return Math.round(Math.random() * 10000000000).toString(); // 0 -- 10000000000
};

var getTimestamp = function () {
    return new Date().getTime().toString();
};

var getRandomAlphas = function (count) {
    count = Number(count);
    if (isNaN(count) || count <= 0) count = 5;
    if (count > 100) count = 100; //maximum
    var charcodes = [];
    for (var i = 0; i < count; ++i)
        charcodes.push((Math.random() * 10000) % 26 + 97);
    return String.fromCharCode.apply(null, charcodes);
};

exports.getRandomWithTimestamp = getRandomWithTimestamp;
exports.getRandom = getRandom
exports.getTimestamp = getTimestamp;
exports.getRandomAlphas = getRandomAlphas;
