var validate = require('validate');
var config = require('config');
var extension = require('extension');

/*
'email':,
'hash_pwd':,
*/
var adjustUsertokenUpdate = function (input) {
    if (input == null || !validate.isObj(input))
        return false;
    var ret = {};
    //email
    if (validate.value(input.email)) {
        if (validate.email(input.email))
            ret.email = input.email;
        else
            return false;
    }
    //hash_pwd
    if (validate.value(input.hash_pwd)) {
        if (validate.hash(input.hash_pwd))
            ret.hash_pwd = input.hash_pwd;
        else
            return false;
    }
    return extension.obj.objLen(ret) == 0 ? false : ret;
};

exports.adjustUsertokenUpdate = adjustUsertokenUpdate;
