
var errdef = require('errdef');
var yzx = require('./yunzhixun.js');

var providers = {
    yzx: {
        //toMobile
        handler: function (options) {
            return yzx.handle({ toMobile: options.toMobile })
            .then(function (ret) {
                return ret;
            })
            .catch(function (err) {
                throw new errdef.SmsError(err);
            });
        },
        name: 'yunZhiXun'
    }
};

exports.getSmsProvider = function () {
    return providers.yzx;
};
