
var randomPics = {
    male: ['boy1.jpg', 'boy2.jpg'],
    female: ['girl1.jpg', 'girl2.jpg'],
    unknown: ['unknown1.jpg', 'unknown2.png']
};

var getUserDefaultHeadPic = function (gender) {
    var list = randomPics[gender] || randomPics.unknown;
    return list[(new Date().getTime()) % list.length];
};

exports.getUserDefaultHeadPic = getUserDefaultHeadPic;