
var randomPics = {
    male: ['/app/image/webclientshared/boy1.jpg', '/app/image/webclientshared/boy2.jpg'],
    female: ['/app/image/webclientshared/girl1.jpg', '/app/image/webclientshared/girl2.jpg'],
    unknown: ['/app/image/webclientshared/unknown1.jpg', '/app/image/webclientshared/unknown2.png']
};

var getUserDefaultHeadPic = function (gender) {
    var list = randomPics[gender] || randomPics.unknown;
    return list[(new Date().getTime()) % list.length];
};

exports.getUserDefaultHeadPic = getUserDefaultHeadPic;